import React from 'react';

class GoogleAuth extends React.Component {

    state = {isSignedIn : null};

    componentDidMount() {
        window.gapi.load('client:auth2', () =>{
            window.gapi.client.init({
                clientId:'144062268697-tbkvqjghrnnpd6q63qbcb74mgqqgb6lf.apps.googleusercontent.com',
                scope:'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({ isSignedIn : this.auth.isSignedIn.get() });
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = () => {
        this.setState({ isSignedIn : this.auth.isSignedIn.get() })
    };

    renderAuthButton() {
        // if (this.state.isSignedIn === null){
        //     return(
        //         <div>I don't know</div>
        //     );
        // } else 
        if(this.state.isSignedIn === true){
            return(
                <button onClick={this.onSignOut} className="ui blue google button">
                    <i className="google icon" />
                        Sign Out
                </button>
            );
        } else {
            return(
                <button onClick={this.onSignIn} className="ui blue google button">
                <i className="google icon" />
                    Sign in with Google
                </button>
            );
        }
    }


    onSignIn = () => {
        this.auth.signIn();
    };

    onSignOut = () => {
        this.auth.signOut();
    };


    render() {
        return(
            <div>
                {this.renderAuthButton()}
            </div>
        );
    }
}

export default GoogleAuth;
